<?php// Include configrequire_once('../conf/config.php');// Check for malicious characters. True = Good, False = VERY MUCH NOT GOODfunction check_for_symbols($string){	$len=strlen($string);	$alowed_chars="abcdefghijklmnopqrstuvwxyz0123456789";	for($i=0;$i<$len;$i++)		if(!strstr($alowed_chars,$string[$i]))			return TRUE;				return FALSE;}// Return USERNAME:PASSWORD as SHA1function sha_password($user,$pass){	$user = strtoupper($user);	$pass = strtoupper($pass);	return SHA1($user.':'.$pass);}if ($_POST['registration']){	// Connect and Select	$auth_bc_new_connect = mysql_connect($auth[db_host],$auth[db_user],$auth[db_pass]);	$selectdb = mysql_select_db($auth[db_name],$auth_bc_new_connect);	if (!$auth_bc_new_connect || !$selectdb)	{		echo "Unable to connet to the database specified in config.";		die;	}	// Chk	$username = $_POST['username'];	$password = sha_password($username,$_POST['password']);	$qry_check_username = mysql_query("SELECT username FROM `account` WHERE username='$username'");	if (check_for_symbols($_POST[password]) == TRUE || check_for_symbols($username) == TRUE || mysql_num_rows($qry_check_username) != 0)	{		echo "Error: Username is invalid or already in use.";	}	else	{		mysql_query("INSERT INTO account (username,sha_pass_hash) VALUES ('$username','$password')");		echo "Account succesfully created!";	}}else{?><style type="text/css"><!--body,td,th {	color: #999933;}body {	background-color: #000000;}--></style><title>Registration Page</title><form action="<?php echo $_SERVER['PHP_SELF'] ?>" method="POST">  <div align="center">    <p>&nbsp;</p>    <p>&nbsp;</p>    <p>&nbsp;</p>    <p><em><strong>Username       <input type="text" name="username">    </strong></em></p>    <p>&nbsp;</p>    <p><em><strong>Password</strong></em>        <input type="password" name="password">    </p>    <p>       <input name="registration" type="submit" value="Register" />    </p>    <p>&nbsp;</p>    <p>&nbsp;</p>    <p>&nbsp;</p>    <p>&nbsp;</p>  </div></form><?php}?>